import React, { useState } from 'react';
import { initializeApp } from "firebase/app";

// Configuração do Firebase (mesma do back-end)
const firebaseConfig = {
  apiKey: "AIzaSyAhRWRui9X82y-0g2vnvys4brMgdVQt34U",
  authDomain: "hometaskapp.firebaseapp.com",
  projectId: "hometaskapp",
  storageBucket: "hometaskapp.firebasestorage.app",
  messagingSenderId: "746880386819",
  appId: "1:746880386819:web:4bc278463460af8e1fcb36",
  measurementId: "G-C91YN5LL1J"
};

// Inicializa o Firebase para uso no front-end
const app = initializeApp(firebaseConfig);

function AuthForm() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isRegistering, setIsRegistering] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    try {
      const endpoint = isRegistering ? '/register' : '/login';
      
      const response = await fetch(`http://localhost:3000${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });
      
      const data = await response.json();
      
      if (!data.success) {
        throw new Error(data.error);
      }
      
      console.log('Sucesso:', data.user);
      // Redirecionar ou atualizar o estado do usuário aqui
      
    } catch (error) {
      setError(error.message);
      console.error('Erro:', error);
    }
  };

  return (
    <div>
      <h2>{isRegistering ? 'Cadastro' : 'Login'}</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="E-mail"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Senha"
          required
        />
        <button type="submit">
          {isRegistering ? 'Cadastrar' : 'Entrar'}
        </button>
      </form>
      <button onClick={() => setIsRegistering(!isRegistering)}>
        {isRegistering ? 'Já tem conta? Faça login' : 'Não tem conta? Cadastre-se'}
      </button>
    </div>
  );
}

export default AuthForm;
